function random(a){return a[Math.floor(Math.random()*a.length)]}function capitalize(a){return""+a.slice(0,1).toUpperCase()+a.slice(1).toLowerCase()}module.exports=function(a){var e=require("../data/adjectives.json"),r=require("../data/animals.json");(a=a||{}).adjectives=a.adjectives||1,a.animals=a.animals||1,a.separator=a.separator||"";for(var t=[],i=0;i<a.adjectives;i++)t.push(capitalize(random(e)));for(i=0;i<a.animals;i++)t.push(capitalize(random(r)));return t.join(a.separator)};